    static class SizeAndWord implements Comparable<SizeAndWord> {
        
        int iSize;
        String sValue;
        
        public SizeAndWord(int isize, String svalue) {
            iSize = isize;
            sValue = svalue;
        }
        
        public int getSize() {
            return iSize;
        }
        
        public String getValue() {
            return sValue;
        }
        
        @Override
        public int compareTo(SizeAndWord t) {
            if (iSize < t.iSize) {
                return -1;
            }
            if (iSize > t.iSize) {
                return 1;
            }
            return 0;
        }
    }
